<?phpnamespace Nemundo\Content\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\Content\Application\ContentApplication;use Nemundo\Content\Index\Geo\Application\GeoIndexApplication;use Nemundo\Content\Index\Log\Application\ContentLogApplication;use Nemundo\Content\Index\Relation\Application\RelationApplication;use Nemundo\Content\Index\Search\Application\SearchIndexApplication;use Nemundo\Content\Index\Tree\Application\TreeIndexApplication;class ContentApplicationInstall extends AbstractInstall{    public function install()    {        (new ApplicationSetup())            ->addApplication(new TreeIndexApplication())            ->addApplication(new GeoIndexApplication())            ->addApplication(new SearchIndexApplication())            ->addApplication(new ContentLogApplication())            ->addApplication(new RelationApplication())            ->addApplication(new ContentApplication());    }}