<?phpnamespace Nemundo\Content\Action\Com;use Muenster\App\Verortung\Action\VerortungActionForm;use Muenster\App\Verortung\Action\VerortungActionView;use Nemundo\Admin\Com\Title\AdminSubtitle;use Nemundo\Admin\Com\Widget\AdminWidget;use Nemundo\Content\Data\ContentAction\ContentActionReader;use Nemundo\Content\Type\AbstractContentType;class ActionWidget extends AdminWidget{    /**     * @var AbstractContentType     */    public $content;    public function getContent()    {        $this->widgetTitle='Action';        $contentActionReader = new ContentActionReader();        foreach ($contentActionReader->getData() as $contentActionRow) {            $action = $contentActionRow->getAction($this->content);            if ($action->hasView()) {                //$widget = new AdminWidget($this);                //$widget->widgetTitle= $contentActionRow->action;                $subtitle = new AdminSubtitle($this);                $subtitle->content = $contentActionRow->action;                $view = $action->getView($this);  // new VerortungActionView($widget);                $view->content = $this->content;                if ($action->hasForm()) {                $form=$action->getForm($this);                $form->content = $this->content;                }            }        }        /*        $view=new VerortungActionView($this);        $view->content=$this->content;        $form=new VerortungActionForm($this);        $form->content=$this->content;*/        return parent::getContent();    }}