<?phpnamespace Nemundo\Content\Action\Setup;use Nemundo\Content\Action\AbstractContentAction;use Nemundo\Content\Data\ContentAction\ContentAction;use Nemundo\Content\Data\ContentAction\ContentActionCount;use Nemundo\Content\Data\ContentAction\ContentActionDelete;use Nemundo\Content\Data\ContentAction\ContentActionUpdate;use Nemundo\Core\Base\AbstractBase;class ActionSetup extends AbstractBase{    public function addContentAction(AbstractContentAction $contentAction)    {        $count = new ContentActionCount();        $count->filter->andEqual($count->model->id, $contentAction->actionId);        if ($count->getCount() === 0) {            $data = new ContentAction();            $data->id = $contentAction->actionId;            $data->action = $contentAction->actionLabel;            $data->phpClass = $contentAction->getClassName();            $data->menuActive = true;            $data->setupStatus = true;            $data->save();        } else {            $update = new ContentActionUpdate();            $update->action = $contentAction->actionLabel;            $update->phpClass = $contentAction->getClassName();            $update->menuActive = true;            $update->setupStatus = true;            $update->updateById($contentAction->actionId);        }        return $this;    }    public function removeContentAction(AbstractContentAction $contentAction)    {        (new ContentActionDelete())->deleteById($contentAction->actionId);        return $this;    }}