<?phpnamespace Nemundo\Content\Com\Dropdown;use Nemundo\Content\Action\ContentActionTrait;use Nemundo\Content\Action\Parameter\ActionParameter;use Nemundo\Content\Data\ContentAction\ContentActionReader;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Content\Site\ContentActionSite;use Nemundo\Package\Bootstrap\Dropdown\BootstrapSiteDropdown;class ContentActionDropdown extends BootstrapSiteDropdown{    public $contentId;    use ContentActionTrait;    public function getContent()    {        //(new Debug())->write($this->contentId);        foreach ($this->getContentActionList() as $contentAction) {            $site = clone(ContentActionSite::$site);            $site->title = $contentAction->actionLabel;            $site->addParameter(new ContentParameter($this->contentId));            //$site->addParameter(new \Nemundo\Content\Action\Parameter\ContentActionParameter($contentAction->typeId));            $site->addParameter(new ActionParameter($contentAction->actionId));            $this->addSite($site);        }        return parent::getContent();    }    public function addDefaultAction()    {        $reader = new ContentActionReader();        $reader->addOrder($reader->model->action);        foreach ($reader->getData() as $actionRow) {            $this->addContentAction($actionRow->getAction(null));        }    }}