<?phpnamespace Nemundo\Content\Index\Geo\Com\ListBox;use Nemundo\Admin\Com\ListBox\AdminListBox;use Nemundo\Content\Index\Geo\Parameter\DistanceParameter;class DistanceListBox extends AdminListBox{    protected function loadContainer()    {        parent::loadContainer();        $this->name = (new DistanceParameter())->getParameterName();        $this->emptyValueAsDefault = true;        $this->label = 'Distance';    }    public function getContent()    {        $this->addItem(0.5, '500m');        $this->addItem(1, '1 km');        $this->addItem(5, '5 km');        $this->addItem(10, '10 km');        $this->addItem(50, '50 km');        return parent::getContent();    }}