ordinate();        //$geoCoordinate->        //$geoCoordinate->fromText('46.8735590368648, 8.635687309394777');        //$geoCoordinateTextBox->setGeoCoordinate($geoCoordinate);        new AdminSearchButton($form);        if ($geoCoordinateTextBox->hasValue()) {            $geoCoordinate = $geoCoordinateTextBox->getGeoCoordinate();            //$geoCoordinate = new GeoCoordinate();            //$geoCoordinate->fromText('46.8735590368648, 8.635687309394777');            //$geoCoordinate->fromText('46.90687596057738, 8.396299802775115');            $btn = new AdminIconSiteButton($layout);            $btn->site = clone(AroundKmlSite::$site);            $btn->site->addParameter(new DistanceParameter());            $btn->site->addParameter(new LatitudeParameter($geoCoordinate->latitude));            $btn->site->addParameter(new LongitudeParameter($geoCoordinate->longitude));            $p = new Paragraph($layout);            $table = new AdminTable($layout);            $header = new TableHeader($table);            $header->addText('Content Type');            $header->addText('Place');            $header->addText('Coordinate');            $header->addText('Distance');            $geoReader = new GeoIndexPaginationReader();            $geoReader->model->loadContent();            $geoReader->model->content->loadContentType();            $filter = new GeoCoordinateSquareFilter();            $filter->coordinateCenter = $geoCoordinateTextBox->getGeoCoordinate();            $filter->coordinateType = $geoReader->model->coordinate;            if ($distance->hasValue()) {                $filter->distanceInKilometres = $distance->getValue();            }            $geoReader->filter->andFilter($filter);            $p->content = $geoReader->getFormatTotalCount() . ' item found';            /*            $contentTypeParameter = new ContentTypeParameter();            $contentTypeParameter->contentTypeCheck = false;            if ($contentTypeParameter->hasValue()) {                $geoReader->filter->andEqual($geoReader->model->content->contentTypeId, $contentTypeParameter->getValue());            }            $geoReader->addOrder($geoReader->model->place);*/            foreach ($geoReader->getData() as $geoRow) {                $contentType = $geoRow->content->getContent();                if ($contentType !== null) {                    $row = new AdminTableRow($table);                    /*if ($geoRow->id == (new GeoIndexParameter())->getValue()) {                        $row->setActiveRow();                    }*/                    $row->addText($contentType->getTypeLabel());                    $row->addText($geoRow->place);                    $row->addText($geoRow->coordinate->getText());                    $geoDistance = new GeoCoordinateDistance();                    $geoDistance->geoCoordinateFrom = $geoCoordinateTextBox;                    $geoDistance->geoCoordinateTo = $geoRow->coordinate;                    $row->addText($geoDistance->getDistanceText());                    /*if ($contentType->hasKmlMarker()) {                        $site = clone(GeoContentKmlSite::$site);                        $site->addParameter(new GeoIndexParameter($geoRow->id));                        $row->addSite($site);                    } else {                        $row->addEmpty();                    }*/                    /*                    $site = clone(GeoIndexSite::$site);                    $site->addParameter(new ContentParameter($contentType->getContentId()));                    $site->addParameter(new ContentTypeParameter());                    $site->addParameter(new PageParameter());                    $site->addParameter(new GeoIndexParameter($geoRow->id));                    $row->addClickableSite($site);*/                } else {                    (new LogMessage())->writeError('No Content Type');                }            }         