eft);        $header = new TableHeader($table);        $header->addText('Place');        $header->addText('Content Type');        $header->addText('Coordinate');        $geoReader = new GeoIndexPaginationReader();        $geoReader->model->loadContent();        $geoReader->model->content->loadContentType();        $contentTypeParameter = new ContentTypeParameter();        $contentTypeParameter->contentTypeCheck = false;        if ($contentTypeParameter->hasValue()) {            $geoReader->filter->andEqual($geoReader->model->content->contentTypeId, $contentTypeParameter->getValue());        }        $p->content = $geoReader->getFormatTotalCount() . ' item found';        $geoReader->currentPage = (new PageParameter())->getValue();        $geoReader->addOrder($geoReader->model->place);        foreach ($geoReader->getData() as $geoRow) {            $row = new AdminTableRow($table);            $site = clone(GeoIndexSite::$site);            $site->title = $geoRow->place;            $site->addParameter(new ContentParameter($geoRow->contentId));            $site->addParameter(new ContentTypeParameter());            $site->addParameter(new PageParameter());            $site->addParameter(new GeoIndexParameter($geoRow->id));            $row->addSite($site);            $row->addText($geoRow->content->contentType->contentType);            $row->addText($geoRow->coordinate->getText());            /*            if ($contentType->hasKmlMarker()) {                $site = clone(GeoContentKmlSite::$site);                $site->addParameter(new GeoIndexParameter($geoRow->id));                $row->addIconSite($site);            } else {                $row->addEmpty();            }*/            /*$site = clone(ContentDeleteSite::$site);            $site->addParameter(new ContentParameter($contentType->getContentId()));            $row->addIconSite($site);*/            //$row->addClickableSite($site);            //} /*else {            /*     (new LogMessage())->writeError('No Content Type');             }*/        }        $pagination = new AdminPagination($left);        $pagination->paginationReader = $geoReader;        /*        $parameter = new ContentTypeParameter();        if ($parameter->hasValue()) {            $contentType = $parameter->getContentType();            if ($contentType->hasForm()) {                $container = new ContentTypeFormContainer($layout->col2);                $container->contentType = $contentType;                $container->redirectSite = clone(GeoIndexSite::$site);                $container->redirectSite->addParameter(new ApplicationParameter());                $container->redirectSite->addParameter(new ContentTypeParameter());            }        }*/        $contentParameter = new ContentParameter();        if ($contentParameter->hasValue()) {            //$content = $contentParameter->getContent(false);            //$content->getDefaultView($right);            $card = new ContentCard($right);            $card->contentId = $contentParameter->getValue();            $table = new AdminTable($right);            $header = new TableHeader($table);            $header->addText('Content');            //$header->addText('Type');            $header->addText('Distance');            $reader = new GeoDistanceReader();            $reader->contentId = $contentParameter->getValue();  //  $this->contentId;            $reader->limit = 10;            foreach ($reader->getData() as $geoDistanceItem) {                $row = new AdminTableRow($table);                //$row->addText($geoDistanceItem->subject);                $site = clone(GeoIndexSite::$site);                $site->addParameter(new ContentParameter($geoDistanceItem->contentId));                $site->title = $geoDistanceItem->subject;                $row->addSite($site);                $row->addText($geoDistanceItem->getDistanceText());                //$row->addText((new Number($distanceRow->distance / 1000))->roundNumber(0) . ' km');                /*if ($this->redirectSite !==null) {                    $site = clone($this->redirectSite);                    $site->addParameter(new ContentParameter($geoDistanceItem->contentId));                    $row->addClickableSite($site);                }*/            }            /*$card = new ContentCard($layout->col2);            $card->content=$content;            /*            $widget = new ContentWidget($layout->col2);            $widget->redirectSite = GeoIndexSite::$site;            $widget->contentType = $content;            //$widget->loadAction=true;            $widget->addContentAction(new EditAction());            $widget->addContentAction(new ViewAction());            $action = new KmlAction();            $widget->addContentAction($action);*/            // geo kml            // empty            /*$container = new GeoIndexContainer($layout->col2);            $container->contentType = $content;            $container->redirectSite = GeoIndexSite::$site;*/        }        return parent::getContent();    }}