<?phpnamespace Nemundo\Content\Index\Geo\Setup;use Nemundo\Content\Data\Content\ContentDelete;use Nemundo\Content\Index\Geo\Data\GeoContentType\GeoContentType;use Nemundo\Content\Index\Geo\Data\GeoContentType\GeoContentTypeDelete;use Nemundo\Content\Index\Geo\Data\GeoIndex\GeoIndexDelete;use Nemundo\Content\Setup\AbstractContentTypeSetup;use Nemundo\Content\Setup\ContentTypeRemove;use Nemundo\Content\Type\AbstractContentType;class GeoContentTypeSetup extends AbstractContentTypeSetup{    public function addContentType(AbstractContentType $contentType)    {        $data = new GeoContentType();        $data->ignoreIfExists = true;        $data->contentTypeId = $contentType->typeId;        $data->save();        //return parent::addContentType($contentType);        return $this;    }    public function removeContentType(AbstractContentType $contentType)    {        $delete = new GeoIndexDelete();        $delete->model->loadContent();        $delete->filter->andEqual($delete->model->content->contentTypeId, $contentType->typeId);        $delete->delete();        $delete = new GeoContentTypeDelete();        $delete->filter->andEqual($delete->model->contentTypeId, $contentType->typeId);        $delete->delete();        (new ContentTypeRemove())->removeContent($contentType);        return $this;        //return parent::removeContentType($contentType);    }}