<?phpnamespace Nemundo\Content\Index\Log\Site;use Nemundo\Content\Index\Log\Page\LogPage;use Nemundo\Web\Site\AbstractSite;class LogSite extends AbstractSite{    /**     * @var LogSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Content Log';        $this->url = 'content-log';        LogSite::$site = $this;    }    public function loadContent()    {        (new LogPage())->render();    }}