<?phpnamespace Nemundo\Content\Index\Log\Type;use Nemundo\Content\Index\Log\View\AbstractContentLogView;use Nemundo\Content\Type\AbstractContentType;use Nemundo\Content\View\AbstractContentAdmin;use Nemundo\Core\Log\LogMessage;use Nemundo\Html\Container\AbstractContainer;abstract class AbstractLogContentType extends AbstractContentType{    public $logViewClass;    public function hasLogView()    {        return $this->hasProperty($this->logViewClass);    }    public function getLogView(AbstractContainer $parent)    {        $view = null;        if ($this->hasLogView()) {            /** @var AbstractContentLogView $view */            $view = new $this->logViewClass($parent);            //$view->contentType = $this;        } else {            (new LogMessage())->writeError('No Log View Class. Class: ' . $this->getClassName());        }        return $view;    }}