<?phpnamespace Nemundo\Content\Index\Search\Action;use Nemundo\Content\Action\AbstractActionView;use Nemundo\Content\Index\Search\Data\SearchIndex\SearchIndexReader;use Nemundo\Core\Directory\TextDirectory;use Nemundo\Html\Paragraph\Paragraph;class SearchActionView extends AbstractActionView{    public function getContent()    {        $reader = new SearchIndexReader();        $reader->model->loadWord();        $reader->filter->andEqual($reader->model->contentId, $this->contentId);        $reader->addOrder($reader->model->word);        $list = new TextDirectory();        foreach ($reader->getData() as $wordRow) {            $list->addValue($wordRow->word->word);        }        $p = new Paragraph($this);        $p->content = $list->getTextWithSeperator();        return parent::getContent();    }}