<?phpnamespace Nemundo\Content\Index\Search\Install;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\App\Script\Setup\ScriptSetup;use Nemundo\App\WebService\Application\WebServiceApplication;use Nemundo\App\WebService\Setup\ServiceRequestSetup;use Nemundo\Content\Action\Setup\ActionSetup;use Nemundo\Content\Application\ContentApplication;use Nemundo\Content\Index\Search\Action\SearchIndexContentAction;use Nemundo\Content\Index\Search\Application\SearchIndexApplication;use Nemundo\Content\Index\Search\Data\SearchModelCollection;use Nemundo\Content\Index\Search\Script\SearchCleanScript;use Nemundo\Content\Index\Search\Script\SearchIndexRepairScript;use Nemundo\Content\Index\Search\Script\WordCleanScript;use Nemundo\Content\Index\Search\Service\ContentSearchService;use Nemundo\Content\Index\Search\Service\WordService;use Nemundo\Model\Setup\ModelCollectionSetup;class SearchIndexInstall extends AbstractInstall{    public function install()    {        (new WebServiceApplication())->installApp();        (new ContentApplication())->installApp();        (new ModelCollectionSetup())            ->addCollection(new SearchModelCollection());        (new ScriptSetup(new SearchIndexApplication()))            ->addScript(new SearchCleanScript())            ->addScript(new SearchIndexRepairScript())            ->addScript(new WordCleanScript());        (new ActionSetup())            ->addContentAction(new SearchIndexContentAction());        (new ServiceRequestSetup(new SearchIndexApplication()))            ->addService(new WordService())            ->addService(new ContentSearchService());    }}