<?phpnamespace Nemundo\Content\Index\Tree\Com\Table;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\AdminTableHeader;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Content\Com\Container\AbstractContentTypeContainer;use Nemundo\Content\Index\Tree\Parameter\TreeParameter;use Nemundo\Content\Index\Tree\Reader\ChildContentReader;use Nemundo\Content\Index\Tree\Reader\ChildContentTypeReader;use Nemundo\Content\Index\Tree\Reader\ChildTreeDataReader;use Nemundo\Content\Index\Tree\Reader\ParentTreeDataReader;use Nemundo\Content\Index\Tree\Site\ChildDeleteSite;use Nemundo\Content\Index\Tree\Site\ViewEditSite;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Content\Site\ContentEditSite;use Nemundo\Content\Site\ContentViewSite;use Nemundo\Package\Bootstrap\Table\BootstrapClickableTableRow;class ParentTreeTable extends AdminTable{    public $contentId;    public function getContent()    {        /*$header = new AdminTableHeader($this);        $header->addText('Child');        $header->addText('Typ');        $header->addText('View');        $header->addText('Item Order');        $header->addEmpty();        $header->addEmpty();*/        new TreeTableHeader($this);        $reader = new ParentTreeDataReader();  // new ChildTreeDataReader();  // new ChildContentReader();        $reader->contentId = $this->contentId;        foreach ($reader->getData() as $treeRow) {            $site = clone(ContentViewSite::$site);            $site->addParameter(new ContentParameter($treeRow->parentId));            $row = new AdminTableRow($this);            $row->addClickableText($treeRow->parent->subject,$site);            $row->addText($treeRow->parent->contentType->contentType);            $row->addText($treeRow->itemOrder);            //$row->addIconSite($site);            $site = clone(ChildDeleteSite::$site);            $site->addParameter(new TreeParameter($treeRow->id));            $row->addIconSite($site);        }        return parent::getContent();    }}