<?phpnamespace Nemundo\Content\Index\Tree\Site;use Nemundo\Admin\Site\AbstractDeleteIconSite;use Nemundo\Com\Template\TemplateDocument;use Nemundo\Content\Index\Tree\Data\Tree\TreeDelete;use Nemundo\Content\Index\Tree\Parameter\TreeParameter;use Nemundo\Content\Parameter\ContentTypeParameter;use Nemundo\Core\Http\Url\UrlReferer;use Nemundo\Web\Site\AbstractSite;class ChildDeleteSite extends AbstractDeleteIconSite{    /**     * @var ChildDeleteSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Remove Content';        $this->url = 'remove-content';        ChildDeleteSite::$site = $this;    }    public function loadContent()    {        $treeId = (new TreeParameter())->getValue();        (new TreeDelete())->deleteById($treeId);        (new UrlReferer())->redirect();    }}