<?phpnamespace Nemundo\Content\Page\Admin;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\AdminTableHeader;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Admin\Com\Title\AdminSubtitle;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Content\Data\ContentAction\ContentActionReader;use Nemundo\Content\Data\ContentIndex\ContentIndexReader;use Nemundo\Content\Template\ContentAdminTemplate;use Nemundo\Package\Bootstrap\Layout\BootstrapTwoColumnLayout;use Nemundo\Package\Bootstrap\Table\BootstrapClickableTableRow;class ActionAdminPage extends ContentAdminTemplate{    public function getContent()    {        $actionReader = new ContentActionReader();        $actionReader->addOrder($actionReader->model->action);        $table = new AdminTable($this);        $header = new AdminTableHeader($table);        $header->addText($actionReader->model->action->label);        $header->addText($actionReader->model->id->label);        foreach ($actionReader->getData() as $contentActionRow) {            $row = new AdminTableRow($table);            $row->addText($contentActionRow->action);            $row->addText($contentActionRow->id);        }        return parent::getContent();    }}