<?phpnamespace Nemundo\Content\Page;use Nemundo\Content\Parameter\ContentTypeParameter;use Nemundo\Content\Template\ContentTemplate;use Nemundo\Html\Paragraph\Paragraph;class ContentAdminPage extends ContentTemplate{    public function getContent()    {        $parameter = new ContentTypeParameter();        if ($parameter->hasValue()) {            $contentType = $parameter->getContentType();            if ($contentType->hasAdmin()) {                $contentType->getAdmin($this);            } else {                $p = new Paragraph($this);                $p->content = 'No Admin';            }        }        return parent::getContent();    }}