<?phpnamespace Nemundo\Content\Parameter;use Nemundo\Content\Builder\ContentBuilder;use Nemundo\Content\Check\ContentTypeCheckTrait;use Nemundo\Content\Type\JsonContentTrait;use Nemundo\Web\Parameter\AbstractUrlParameter;abstract class AbstractContentUrlParameter extends AbstractUrlParameter{    use ContentTypeCheckTrait;    /**     * @param false $checkContentType     * @return \Nemundo\Content\Index\Geo\Type\GeoIndexTrait|\Nemundo\Content\Type\AbstractContentType|JsonContentTrait     */    public function getContent($checkContentType = false)    {        $contentType = (new ContentBuilder())->getContent($this->getValue());        if ($checkContentType) {            $this->checkContentType($contentType);        }        return $contentType;    }    public function getContentItem()    {        $item = (new ContentBuilder($this->getValue()))->getContentItem();        return $item;    }    /*public function getContentRemove()    {        $item = (new ContentBuilder($this->getValue()))->getContent() getContentItem();        return $item;    }*/}