<?phpnamespace Nemundo\Content\Setup;use Nemundo\Content\Collection\AbstractContentTypeCollection;use Nemundo\Content\Type\AbstractContentType;class ContentTypeSetup extends AbstractContentTypeSetup{    public function addContentType(AbstractContentType $contentType)    {        parent::addContentType($contentType);        return $this;    }    public function addContentTypeCollection(AbstractContentTypeCollection $collection)    {        foreach ($collection->getContentTypeList() as $contentType) {            $this->addContentType($contentType);        }        return $this;    }}