<?phpnamespace Nemundo\Content\Site\Ajax;use Nemundo\Content\Parameter\ContentTypeParameter;use Nemundo\Core\Debug\Debug;use Nemundo\Core\Http\Response\HttpResponse;use Nemundo\Web\Site\AbstractSite;class ContentTypeFormAjaxSite extends AbstractSite{    /**     * @var ContentTypeFormAjaxSite     */    public static $site;    protected function loadSite()    {        $this->url = 'content-type-form-ajax';        $this->menuActive = false;        ContentTypeFormAjaxSite::$site = $this;    }    public function loadContent()    {        $contentType = (new ContentTypeParameter())->getContentType();        if ($contentType->hasForm()) {            $form = $contentType->getDefaultForm();            $form->submitForm=false;            $html = $form->getBodyContent();            $response = new HttpResponse();            $response->content = $html;            $response->sendResponse();        } else {            (new Debug())->write('No Form');        }    }}