<?phpnamespace Nemundo\Content\Site\Ajax;use Nemundo\Content\Page\ContentViewPage;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Content\Parameter\ContentTypeParameter;use Nemundo\Core\Debug\Debug;use Nemundo\Core\Http\Response\HttpResponse;use Nemundo\Package\FontAwesome\Icon\ViewIcon;use Nemundo\Package\FontAwesome\Site\AbstractIconSite;use Nemundo\Web\Site\AbstractSite;class ContentViewAjaxSite extends AbstractSite{    /**     * @var ContentViewAjaxSite     */    public static $site;    protected function loadSite()    {        $this->url = 'content-ajax_view';        //$this->icon = new ViewIcon();        $this->menuActive = false;        ContentViewAjaxSite::$site = $this;    }    public function loadContent()    {        // view Id        $content = (new ContentParameter())->getContent();        if ($content->hasView()) {            $form = $content->getDefaultView();            $html = $form->getBodyContent();            $response = new HttpResponse();            $response->content = $html;            $response->sendResponse();        } else {            (new Debug())->write('No View');        }    }}