<?phpnamespace Nemundo\Content\Site\Json;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Web\Site\AbstractJsonSite;class ContentJsonSite extends AbstractJsonSite{    /**     * @var ContentViewJsonSite     */    public static $site;    protected function loadSite()    {        $this->url = 'content-json';        ContentJsonSite::$site = $this;    }    protected function loadJson()    {        $content = (new ContentParameter())->getContent(false);        $this->setJsonData($content->getJsonData());    }}