<?phpnamespace Nemundo\Content\Type;use Nemundo\Content\Builder\IndexBuilder;use Nemundo\Core\Base\AbstractBase;abstract class AbstractContentRemove extends AbstractBase{    /**     * @var AbstractContentType     */    protected $contentType;    protected $dataId;    abstract protected function loadRemove();    // onRemove    abstract protected function onDelete();    public function __construct($dataId)    {        $this->dataId = $dataId;        $this->loadRemove();    }    public function removeItem()    {        $item = $this->contentType->getItem($this->dataId);        (new IndexBuilder())->deleteIndex($item);        $this->onDelete();    }}