<?phpnamespace Nemundo\Content\Type;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Content\Form\AbstractContentFormPart;use Nemundo\Html\Container\AbstractContainer;trait FormPartTrait{    /**     * @var string     */    protected $formPartClass;    public function hasFormPart() {        $value=false;        if ($this->formPartClass!==null) {            $value=true;        }        return $value;    }    public function getFormPart(AbstractContainer $parent = null)    {        /** @var AbstractContentFormPart $formPart */        $formPart = new $this->formPartClass($parent);        $formPart->contentType = $this;        return $formPart;    }}